import React, { Component } from "react";
import { Link, withRouter } from "react-router-dom";
import { connect } from "react-redux";
import Banner from "../../assets/img/bg-banner1.jpg";
import { getNumAllPosts } from "../../actions/post.action";
import PropTypes from "prop-types";
import AutoCompleteText from "../HOC/AutoCompleteText";
import classnames from "classnames";

class Search extends Component {
  constructor(props) {
    super(props);

    this.state = {
      isShow: false,
      numAllPosts: 0,
    };
  }

  async componentDidMount() {
    await this.props.getNumAllPosts({ type: "all", numPosts: 0 });
    this.setState({
      ...this.state,
      numAllPosts: this.props.posts.numAllPosts,
    });
  }

  render() {
    const { numAllPosts } = this.state;

    return (
      <header
        className="site-header size-lg text-center"
        style={{
          backgroundImage: { Banner },
        }}
      >
        <div className="container">
          <div className="col-xs-12">
            <br />
            <br />
            <h2>
              Hiện tại đang có <mark>{numAllPosts}</mark> công việc trên hệ
              thống
            </h2>
            <h5 className="font-alt">
              Tìm công việc bạn mong muốn trên hệ thống
            </h5>
            <br />
            <br />
            <br />
            <form className="header-job-search">
              <div className="input-keyword">
                <input
                  type="text"
                  className="form-control"
                  placeholder="Job title, skills, or company"
                />
              </div>

              <div className="input-location">
                <input
                  type="text"
                  className="form-control"
                  placeholder="City, state or zip"
                />
              </div>

              <div className="btn-search">
                <button className="btn btn-primary" type="submit">
                  Tìm kiếm công việc
                </button>
                <a href="job-list.html">Tìm kiếm nâng cao</a>
              </div>
            </form>
          </div>
        </div>
      </header>
    );
  }
}

Search.propTypes = {
  posts: PropTypes.object.isRequired,
  getNumAllPosts: PropTypes.func.isRequired,
};

const mapStateToProps = (state) => ({ posts: state.posts });

const mapDispatchToProps = { getNumAllPosts };

export default connect(mapStateToProps, mapDispatchToProps)(Search);
