import { GET_NUM_POSTS, GET_POSTS } from '../actions/actionTypes';

const initialState = {
    error: '',
    postData: [],
    params: {},
    getType: "latest",
    numAllPosts: 0,
};

export default (state = initialState, {type, payload}) => {
    switch (type) {
        case GET_POSTS:
            return {
                ...state,
                postData: payload.results.response,
                params: payload.params,
                getType: payload.results.getType,
            };
        case GET_NUM_POSTS:
            return {
                ...state,
                numAllPosts: payload.numAllPosts
            }
        default:
            return state;
    }
}
