import React, { Component } from "react";
import { Link, withRouter } from "react-router-dom";
import { connect } from "react-redux";
import Banner from "../../assets/img/bg-banner1.jpg";
import { getPosts } from "../../actions/post.action"
import classnames from "classnames";

class Search extends Component {
  constructor(props) {
    super(props);

    this.state = {
      isShow: false,
      numAllPosts: 0
    };
  }

  componentDidMount() {
    this.props.getPosts({type: "all", numPosts: 0});
    this.setState({
      ...this.state,
      numAllPosts: this.props.posts.numPosts
    })
  }

  render() {
    return (
      <header
        className="site-header size-lg text-center"
        style={{
          backgroundImage: {Banner},
        }}
      >
        <div className="container">
          <div className="col-xs-12">
            <br />
            <br />
            <h2>
              We offer <mark>1,259</mark> job vacancies right now!
            </h2>
            <h5 className="font-alt">Find your desire one in a minute</h5>
            <br />
            <br />
            <br />
            <form className="header-job-search">
              <div className="input-keyword">
                <input
                  type="text"
                  className="form-control"
                  placeholder="Job title, skills, or company"
                />
              </div>

              <div className="input-location">
                <input
                  type="text"
                  className="form-control"
                  placeholder="City, state or zip"
                />
              </div>

              <div className="btn-search">
                <button className="btn btn-primary" type="submit">
                  Find jobs
                </button>
                <a href="job-list.html">Advanced Job Search</a>
              </div>
            </form>
          </div>
        </div>
      </header>
    );
  }
}

// export default Header;
const mapStateToProps = (state) => ({ posts });

const mapDispatchToProps = { getPosts };

export default connect(mapStateToProps, mapDispatchToProps)(Search);
