import { GET_POSTS } from './actionTypes';
import axios from 'axios';

export const getPosts = (params) => async dispatch => {
    // let res;
    // if (params.type = "latest") {
    //     res = await axios.post(`/api/here`, params);
    // }
    console.log(params)

    dispatch({
        type: GET_POSTS,
        payload: {
            // results: {"response": res.data, "type": params.type},
            results: {
                "response": [
                    {title: "", companyTitle: "", img: "", address: "", salary: ""}
                ], 
                "type": params.getType},
            params: params
        }
    })
}
