import { GET_POSTS } from '../actions/actionTypes';

const initialState = {
    error: '',
    postData: [],
    params: {},
    type: "latest"
};

export default (state = initialState, {type, payload}) => {
    switch (type) {
        case GET_POSTS:
            return {
                ...state,
                postData: payload.results.response,
                params: payload.params,
                type: payload.results.type
            };
        default:
            return state;
    }
}
